var months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
var days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
window.setTimeout("waktu()", 1000);
function waktu() {
	var waktu = new Date();
	setTimeout("waktu()", 1000);
	document.getElementById("real-time").innerHTML = '<b><em>'+days[waktu.getDay()]+'</em></b><br>'+months[waktu.getMonth()]+', '+waktu.getDate()+' '+waktu.getFullYear()+'<span class="mx-3"> '+waktu.getHours()+' : '+waktu.getMinutes()+' : '+waktu.getSeconds()+'</span>';
}
var win = navigator.platform.indexOf('Win') > -1;
if (win && document.querySelector('#sidenav-scrollbar')) {
var options = {
 	damping: '0.5'
}
Scrollbar.init(document.querySelector('#sidenav-scrollbar'), options);
}
$('#iconSidenav').click(function(){
	$('body').removeClass('g-sidenav-pinned')
	$('#iconSidenav').addClass('d-none')
	$('aside').removeClass('open')
})
$('#iconNavbarSidenav').click(function(){
	$('body').toggleClass('g-sidenav-pinned')
	$('#iconSidenav').toggleClass('d-none')
	$('aside').toggleClass('open')
})